settings.custom_liquid:
{% assign current_day = "now" | date: "%A" %}
{% case current_day %}
    {% when "Monday" %}
        {% assign start_date = "now" | date: "%s" | plus: 86400 | date: "%b %d" %}
        {% assign end_date = "now" | date: "%s" | plus: 172800 | date: "%b %d" %}
    {% when "Tuesday" %}
        {% assign start_date = "now" | date: "%s" | plus: 86400 | date: "%b %d" %}
        {% assign end_date = "now" | date: "%s" | plus: 172800 | date: "%b %d" %}
    {% when "Wednesday" %}
        {% assign start_date = "now" | date: "%s" | plus: 86400 | date: "%b %d" %}
        {% assign end_date = "now" | date: "%s" | plus: 172800 | date: "%b %d" %}
    {% when "Thursday" %}
        {% assign start_date = "now" | date: "%s" | plus: 86400 | date: "%b %d" %}
        {% assign end_date = "now" | date: "%s" | plus: 172800 | date: "%b %d" %}
    {% when "Friday" %}
        {% assign start_date = "now" | date: "%s" | plus: 86400 | date: "%b %d" %}
        {% assign end_date = "now" | date: "%s" | plus: 259200 | date: "%b %d" %}
    {% when "Saturday" %}
        {% assign start_date = "now" | date: "%s" | plus: 172800 | date: "%b %d" %}
        {% assign end_date = "now" | date: "%s" | plus: 259200 | date: "%b %d" %}
    {% else %}
        {% assign start_date = "now" | date: "%s" | plus: 259200 | date: "%b %d" %}
        {% assign end_date = "now" | date: "%s" | plus: 345600 | date: "%b %d" %}
{% endcase %}

<style>
    /* Default styles for mobile */
    .delivery-info {
        text-align: center;
        margin: -10px 0 0 !Important;
        font-size: 80%;
    }

    /* Styles for desktop */
    @media (min-width: 768px) { /* Adjust this breakpoint as needed for your theme */
        .delivery-info {
            text-align: left;
        }
    }
</style>

<div class="delivery-info">
    <img src="https://cdn.shopify.com/s/files/1/0765/7780/0495/files/truck.svg?v=1693062306" alt="Delivery Truck" style="width: 20px; height: 20px; vertical-align: middle;">
    Fast & Tracked Delivery: <span style="color: #BA2253;">{{ start_date }}</span> - <span style="color: #BA2253;">{{ end_date }}</span>
</div>
